mapper.update:
UPDATE ref_user_ids
SET last_id = last_id + 1
WHERE user_type_id = #{id}

mapper.select:
SELECT A.*, l.grade_level AS gradeLevelDesc, section2.section AS sectionDesc FROM data_students A LEFT JOIN ref_grade_level l on A.grade_level_id = l.id
LEFT JOIN ref_section section2 on cast(a.section AS INTEGER) = section2.id
WHERE A.id = #{id}

mapper.update:
UPDATE data_users
SET username = #{user.username},
password = #{user.password},
email = #{user.email}
WHERE id = #{user.id, jdbcType=VARCHAR}

UPDATE data_guidance
SET teacher_fname = #{guidance.firstName, jdbcType=VARCHAR},
teacher_lname = #{guidance.lastName, jdbcType=VARCHAR},
position = #{guidance.position, jdbcType=VARCHAR},
contact_no = #{guidance.contactNo, jdbcType=VARCHAR}
WHERE id = #{guidance.id, jdbcType=VARCHAR}

mapper.insert:
INSERT INTO log_messages( posted_by, message_type, message_target, message_content, date_posted) VALUES ( #{mj.postedBy, jdbcType=VARCHAR}, #{mj.messageTypeId, jdbcType=INTEGER},
#{id}
, #{mj.message, jdbcType=VARCHAR}, NOW() )

mapper.select:
SELECT TOP 1 * FROM log_taps
WHERE rfid = #{rfid}
ORDER BY log_datetime DESC

mapper.update:
UPDATE data_parents
SET sms_notif = #{mode}
WHERE id = #{parentId}

mapper.select:
SELECT A.log_type, A.log_datetime, B.first_name, B.last_name FROM LOG_TAPS A JOIN data_students B ON A.rfid = B.rfid
WHERE B.id = #{studentId}
order by A.log_datetime desc

SELECT DISTINCT office_no FROM data_parents
WHERE sms_notif = true
AND parent_of IN (


        SELECT id FROM data_students WHERE
        section = #{sectionId}


        #{studentId}


)

        SELECT * FROM log_messages A
WHERE posted_by = #{userId}
OR message_type = 3
OR(message_type = 2
AND #{userId} IN (
  SELECT id FROM data_parents B WHERE parent_of IN (
    SELECT id from data_students C WHERE A.message_target LIKE '%' || C.section || '%'
    )
  )
)
OR(message_type = 1
AND #{userId} IN (
    SELECT id FROM data_parents B WHERE parent_of IN(
      SELECT id from data_students D WHERE A.message_target LIKE '%' || CAST(D.grade_level_id AS VARCHAR) || '%'
    )
  )
)
OR(message_type = 0
AND #{userId} IN (
    SELECT id FROM data_parents B where parent_of IN(
      SELECT id from data_students E WHERE A.message_target LIKE '%' || E.id || '%'
    )
  )
)

SELECT *, (first_name || ' ' || last_name) AS fullName FROM data_students
WHERE first_name LIKE #{searchString}
OR last_name LIKE #{searchString}
OR middle_name LIKE #{searchString}
